import React from 'react'
import axios from 'axios';

const AddStuff = () => {
    const[stuff, setStuff] = React.useState({
        name: '',
        foto: '',
        price: '',
        size: '',
        mass: '',
        description: '',
        producer: ''
    })

    function handleChange(event){
        const {value, name} = event.target
        setStuff(prev => ({
            ...prev, [name]: value
        }))
        function getName(){
            console.log(event.target.name) 
            if(event.target.name === 'foto'){
            const [file] = event.target.files
            setStuff({
                foto: file.name
            })
            }
        }
        getName()
        console.log(stuff)
    }

    async function btnAdd(event){
        const obj = stuff
        const req = await axios.put('http://127.0.0.1:3001/addStuff', obj)
        console.log(req)
        event.preventDefault()
    }
    return ( 
        <div>
            Добавление товара
            <input type="text" placeholder="название" name='name' onChange={handleChange}/>
            фото
            <input type="file" name='foto' onChange={handleChange}/>
            <input type="text" placeholder="цена" name='price'  onChange={handleChange}/>
            <input type="text" placeholder="Размеры" name='size'  onChange={handleChange}/>
            <input type="text" placeholder="масса в кг" name='mass'  onChange={handleChange}/>
            Описание
            <textarea cols="30" rows="10" name='description'  onChange={handleChange}></textarea>
            <input type="text" placeholder="продавец" name='description'  onChange={handleChange}/>
            <button onClick={btnAdd}>Добавить товар</button>
        </div>
     );
}
 
export default AddStuff;
