import React from 'react'
import axios from 'axios'

const Registration = () => {

    const [isRegistered, setIsRegisteres] = React.useState(false)
    const [registerForm, setRegisterForm] = React.useState({ 
        name: "",
        password: "",
        role: ""
    })

    function handleChange(event){
        const {value, name} = event.target
        setRegisterForm(prev => ({
            ...prev, [name]: value
        }))
    }

    function getRole (event){
        if(event.target.id === 1){
            setRegisterForm({
                role: "producer"
            })
        }
        else{
            setRegisterForm({
                role: "user"
            })
        }
    }
    
    function btnRegistration(event){
        try {
            const obj = {
                name: registerForm.name,
                password: registerForm.password,
                role: registerForm.role
            }
            // const req =  axios.put('http://127.0.0.1:3001/register', obj)
            console.log(obj)
            // req.then((response)=> {
            //     if(response.data == 'зарегестрирован'){
            //         setIsRegisteres(true)
            //     }
            // })
        } catch (error) {
            alert('Ошибка')
        }
        event.preventDefault()
    }
    return ( 
        <div>
            Регистрация
            <form>
                <input type="text" placeholder="Имя" onChange={handleChange}  value={registerForm.name} name='name'/>
                <input type="password" placeholder="Пароль" onChange={handleChange} value={registerForm.password} name='password'/>
                <label htmlFor="producer">      
                    Продавец         
                    <input type="checkbox" name="producer" id="1" onChange={getRole}/>
                </label>
                <label htmlFor="user">
                    Покупатель
                    <input type="checkbox" name="user" id="2" onChange={getRole}/>
                </label>
                <input type="submit" onClick={btnRegistration} />
            </form>
            {isRegistered ? 'Зареган': 'не зареган'}
        </div>
     );
}
 
export default Registration;
