const Catalog = () => {
    React.useEffect(() =>  
    async function fetchData(){
        // пост запрос на добавление в корзину
        const cartResponse = await axios.get('https://660572ef2ca9478ea180554c.mockapi.io/cart')
        // гет запрос на получение избранных
        const favoriteResponse = await axios.get('https://660a3a2e0f324a9a28845e70.mockapi.io/favorite')
        // гет запрос на вывод кроссовок
        const itemsResponse = await axios.get('https://660572ef2ca9478ea180554c.mockapi.io/items')
  
        setIsLoading(false);
  
        setCartItems(cartResponse.data)
        setFavorites(favoriteResponse.data)
        setItems(itemsResponse.data)
      }, [])
   


    return ( 
        <div>
            Каталог продуктов
