import React from 'react';
import axios from 'axios'
import Card from '../../components/Card';


const Catalog = () => {
    const[items, setItems] = React.useState([]);

    React.useEffect(() =>  
    async function fetchData(){
        const items = await axios.get('http://127.0.0.1:3001/galary')
        setItems(items.data)
        
      }, [])
   
    const renderItems = () => {
    const filteredItems = items
    return (filteredItems).map((item, index)=>(
        <Card
        key={index}
        onFavorite={onAddToFavorite}
        onPlus={(obj) => onAddToCart(obj)} // onPlus функция для отображения в корзину
        loading = {isLoading}
        {...item}
        />
    ))
    }

    return ( 
        <div>
            Каталог продуктов
            <Card items={items}/>
        </div>
     );
}
 
export default Catalog;
