import React, { useState } from 'react'
import axios  from 'axios'

const Login = () => {
    const [loginForm, setLoginForm] = useState({
        email: "",
        password: ""
    })

    function btnLogin(event){
        axios({
            method: "POST",
            url: "https://localhost:3001/login",
            data:{
                email: loginForm.email,
                password: loginForm.password
            }
        })
        .then((response) =>{
            console.log(response)
        }).catch((error) => {
            if(error.response){
                console.log(error.response)
            }
        })
        event.preventDefault()
    }

    function handleChange(event){
        const {value, name} = event.target
        setLoginForm(prev => ({
            ...prev, [name]: value
        }))
    }

    return ( 
        <form>
            <input type="text"  onChange={handleChange} placeholder="Логин"/>
            <input type="password" value={loginForm.password}  onChange={handleChange} placeholder="Пароль"/>
            <input type="submit" onClick={btnLogin} />
        </form>
     );
}
 
export default Login;
